function varargout = mckenzie2021(varargin)
    warning(sprintf(['mckenzie2021 has been renamed mckenzie2022.\n', ...
            'The mckenzie2021 scripts are going to be removed with the next update.\n', ...
            'Calling mckenzie2022...']))
    varargout = mckenzie2022(varargin);
end

